def scan_location(ch,string):
    return [pos for pos, char in enumerate(string) if char == ch]


#s = "ababcdabcd"
#s = "abcd"
#s = "abab"
#s = "abcdefghijklmnopqrstuvwxyz"
s = ""
out = ""
count = 0
i = 0
while i <= len(s)-1:
    t_count_max = 0
    if s[i] in out:
        for j in scan_location(s[i],out):
            t_count = 0
            k = i
            l = j
            while l<=len(out)-1 and k<=len(s)-1 and out[l] == s[k]:
                k=k+1
                l=l+1
                t_count=t_count+1
            if t_count > t_count_max:
                t_count_max = t_count

        out = out + s[i:i+t_count_max]
        i = i + t_count_max
        
    else:
        count=count+1
        out = out + s[i]
        i=i+1

print(count)
